<section id="appointment" class="ds page_appointment background_cover section_padding_top_100 section_padding_bottom_100">
    <div class="container">
        <div class="row">
            <div class="col-sm-12 text-center to_animate" data-animation="fadeInUp">
                <h2 class="section_header with_icon icon_color3">
                    Book Mobile Grooming
                </h2>
                <p class="small-text">best Pet salon in iowa</p>

                <form class="contact-form row columns_margin_bottom_20" method="post" action="./">


                    <div class="col-md-4">
                        <div class="form-group">
                            <label for="pickup-name">Full Name
                                <span class="required">*</span>
                            </label>
                            <i class="fa fa-user highlight3" aria-hidden="true"></i>
                            <input type="text" aria-required="true" size="30" value="" name="pickup-name" id="pickup-name" class="form-control" placeholder="Full Name">
                        </div>
                    </div>
                    <div class="col-md-4">
                        <div class="form-group">
                            <label for="pickup-phone">Phone Number
                                <span class="required">*</span>
                            </label>
                            <i class="fa fa-phone highlight3" aria-hidden="true"></i>
                            <input type="text" aria-required="true" size="30" value="" name="pickup-phone" id="pickup-phone" class="form-control" placeholder="Phone Number">
                        </div>
                    </div>
                    <div class="col-md-2">
                        <div class="form-group select-group">
                            <label for="pickup-date">Date
                                <span class="required">*</span>
                            </label>
                            <i class="fa fa-calendar highlight3" aria-hidden="true"></i>
                            <select aria-required="true" id="pickup-date" name="pickup-date" class="choice empty form-control">
                                <option value="" disabled selected data-default>Date</option>
                                <option value="1306">13.06</option>
                                <option value="1406">14.06</option>
                                <option value="1506">15.06</option>
                                <option value="1606">16.06</option>
                                <option value="1706">17.06</option>
                                <option value="1806">18.06</option>
                                <option value="1906">19.06</option>
                                <option value="2006">20.06</option>
                            </select>
                            <i class="fa fa-angle-down theme_button" aria-hidden="true"></i>
                        </div>
                    </div>
                    <div class="col-md-2">
                        <div class="form-group select-group">
                            <label for="pickup-time">Time
                                <span class="required">*</span>
                            </label>
                            <i class="fa fa-clock-o highlight3" aria-hidden="true"></i>
                            <select aria-required="true" id="pickup-time" name="pickup-time" class="choice empty form-control">
                                <option value="" disabled selected data-default>Time</option>
                                <option value="08am">8:00 AM</option>
                                <option value="09am">9:00 AM</option>
                                <option value="10am">10:00 AM</option>
                                <option value="11am">11:00 AM</option>
                                <option value="12am">12:00 PM</option>
                                <option value="13pm">13:00 PM</option>
                                <option value="14pm">14:00 PM</option>
                                <option value="15pm">15:00 PM</option>
                                <option value="16pm">16:00 PM</option>
                                <option value="17pm">17:00 PM</option>
                            </select>
                            <i class="fa fa-angle-down theme_button" aria-hidden="true"></i>
                        </div>
                    </div>
                    <div class="col-sm-12">

                        <div class="form-group">
                            <label for="pickup-message">Message</label>
                            <i class="fa fa-comment highlight3" aria-hidden="true"></i>
                            <textarea aria-required="true" rows="3" cols="45" name="pickup-message" id="pickup-message" class="form-control" placeholder="Message"></textarea>
                        </div>
                    </div>

                    <div class="col-sm-12">

                        <div class="contact-form-submit topmargin_10">
                            <button type="submit" id="pickup-_submit" name="contact_submit" class="theme_button color3 wide_button">Make an Appointment</button>
                        </div>
                    </div>


                </form>

            </div>
        </div>
    </div>
</section>
